mapper.select:
SELECT 

FROM c_cgzbinfo a

WHERE a.id = #{id}

SELECT

FROM c_cgzbinfo a

WHERE a.goodsid = #{param1} AND a.state = #{param2}

SELECT 

FROM c_cgzbinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.goodsid = #{goods.id}


		AND a.rk_date > #{startDate}


	AND a.rk_date < #{endDate}







		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.goodsid AS "goods.id", cg.name AS "goods.name", sao.name AS "goods.gclass.name", sat.name AS "goods.gclass.parent.name", a.nub AS "nub", SUM(a.nub) AS "nub", SUM(a.jg) AS "jg" FROM c_cgzbinfo a LEFT JOIN c_goods cg ON a.goodsid=cg.id LEFT JOIN c_gclass sao ON sao.id=cg.gclassid LEFT JOIN c_gclass sat ON sat.id=sao.parent_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.goodsid = #{goods.id}
AND a.rk_date > #{startDate}
AND a.rk_date < #{endDate}
GROUP BY sao.name
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_cgzbinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_cgzbinfo a SET
	a.state = #{param2}
WHERE id = #{param1}

UPDATE c_cgzbinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
