mapper.select:
SELECT 

FROM c_ckinfo a

WHERE a.id = #{id}

SELECT 

FROM c_ckinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.supplierid = #{supplier.id}


		AND a.goodsid = #{goods.id}


		AND a.goodsid = #{goods.id}


		AND a.housid = #{house.id}


		AND FIND_IN_SET(a.state,#{state})





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.ddinfo AS "ddinfo.id", mc.ddbh AS "ddinfo.ddbh", mc.thsl AS "ddinfo.thsl", mc.thje AS "ddinfo.thje", a.housid AS "house.id", ch.name AS "house.name", a.storeid AS "store.id", cst.name AS "store.name", sao.name AS "store.area.name", sat.name AS "store.area.parent.name", a.storeid AS "store.id", cst.name AS "store.name", a.goodsid AS "goods.id", cg.name AS "goods.name", cg.specid AS "goods.spec.id", SUM(cg.sj) AS "goods.sj", csp.name AS "goods.spec.name", SUM(a.nub) AS "nub", SUM( cg.cbj) AS "goods.cbj" FROM c_ckinfo a LEFT JOIN c_goods cg ON a.goodsid=cg.id LEFT JOIN c_store cst ON a.storeid=cst.id LEFT JOIN c_spec csp ON cg.`specid`=csp.`id` LEFT JOIN sys_area sao ON sao.id=cst.areaid LEFT JOIN sys_area sat ON sat.id=sao.parent_id LEFT JOIN c_house ch ON a.housid=ch.id LEFT JOIN c_ddinfo mc on a.ddinfo=mc.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.supplierid = #{supplier.id}
AND a.goodsid = #{goods.id}
AND a.goodsid = #{goods.id}
AND a.housid = #{house.id}
AND FIND_IN_SET(a.state,#{state})
GROUP BY cst.name
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT a.ddinfo AS "ddinfo.id", mc.ddbh AS "ddinfo.ddbh", mc.thsl AS "ddinfo.thsl", mc.thje AS "ddinfo.thje", mc.yhje AS "ddinfo.yhje", a.housid AS "house.id", ch.name AS "house.name", a.storeid AS "store.id", cst.name AS "store.name", sao.name AS "store.area.name", sat.name AS "store.area.parent.name", a.storeid AS "store.id", cst.name AS "store.name", a.goodsid AS "goods.id", cg.name AS "goods.name",
cgs.name AS "goods.bands.name", cg.specid AS "goods.spec.id", SUM(cg.sj) AS "goods.sj", csp.name AS "goods.spec.name",
SUM(a.nub) AS "nub", SUM( cg.cbj) AS "goods.cbj" FROM c_ckinfo a LEFT JOIN c_goods cg ON a.goodsid=cg.id LEFT JOIN c_store cst ON a.storeid=cst.id LEFT JOIN c_spec csp ON cg.`specid`=csp.`id` LEFT JOIN c_bands cgs ON cgs.id=cg.bandsid LEFT JOIN sys_area sao ON sao.id=cst.areaid LEFT JOIN sys_area sat ON sat.id=sao.parent_id LEFT JOIN c_house ch ON a.housid=ch.id LEFT JOIN c_ddinfo mc on a.ddinfo=mc.id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.supplierid = #{supplier.id}
AND a.goodsid = #{goods.id}
AND a.goodsid = #{goods.id}
AND a.housid = #{house.id}
AND FIND_IN_SET(a.state,#{state})
GROUP BY cgs.name
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_ckinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_ckinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
