mapper.select:
SELECT 

FROM c_ddinfo a

WHERE a.id = #{id}

SELECT 

FROM c_ddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.rkckddinfoid = #{rkckddinfo.id}


		AND crkck.state = #{rkckddinfo.state}


		AND a.hous_id = #{house.id}


		AND a.goods_id = #{goods.id}


		AND a.id = #{id}


		AND a.supplier_id = #{supplier.id}


		AND a.store_id = #{store.id}


		AND a.cgzbinfo = #{cgzbinfo.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT a.create_by AS "createBy.id", su.name AS "createBy.name" FROM c_ddinfo a LEFT JOIN sys_user su ON su.id=a.create_by LEFT JOIN c_rkckddinfo crk ON crk.id=a.rkckddinfoid
a.del_flag = #{DEL_FLAG_NORMAL} AND su.del_flag = #{DEL_FLAG_NORMAL}
AND FIND_IN_SET(crk.state,#{type})
AND a.rkckdate > #{startDate}
AND a.rkckdate < #{endDate}
GROUP BY a.create_by

SELECT a.goods_id AS "goods.id", cg.name AS "goods.name", cg.sj AS "goods.sj", cg.big AS "goods.big.id", cuna.name AS "goods.big.name", cg.zong AS "goods.zong.id", cunb.name AS "goods.zong.name", cg.small AS "goods.small.id", cunc.name AS "goods.small.name", sp.id AS "goods.spec.id", sp.name AS "goods.spec.name" FROM c_ddinfo a LEFT JOIN c_goods cg ON cg.id=a.goods_id LEFT JOIN c_rkckddinfo crk ON crk.id=a.rkckddinfoid LEFT JOIN c_spec sp ON sp.id=cg.specid LEFT JOIN c_unit cuna ON cg.`big`=cuna.`id` LEFT JOIN c_unit cunb ON cg.`zong`=cunb.`id` LEFT JOIN c_unit cunc ON cg.`small`=cunc.`id`
a.del_flag = #{DEL_FLAG_NORMAL}
AND FIND_IN_SET(crk.state,#{type})
AND a.rkckdate > #{startDate}
AND a.rkckdate < #{endDate}
GROUP BY a.goods_id

SELECT SUM(a.je) AS "je", SUM(a.nub) AS "nub", SUM(a.thje) AS "thje", SUM(a.thsl) AS "thsl" FROM c_ddinfo a LEFT JOIN c_rkckddinfo crk ON crk.id=a.rkckddinfoid
a.del_flag = #{DEL_FLAG_NORMAL} AND a.create_by = #{createBy.id}
AND FIND_IN_SET(crk.state,#{type})
AND a.rkckdate > #{startDate}
AND a.rkckdate < #{endDate}
GROUP BY a.goods_id
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
	SUM(a.je*a.nub) AS "je",
	SUM(a.thje*a.thsl) AS "thje"
FROM c_ddinfo a
LEFT JOIN c_rkckddinfo crk ON crk.id=a.rkckddinfoid

	a.del_flag = #{DEL_FLAG_NORMAL} AND a.create_by = #{createBy.id}

		AND FIND_IN_SET(crk.state,#{type})


		AND a.rkckdate > #{startDate}


		AND a.rkckdate < #{endDate}


GROUP BY a.create_by


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM c_ddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.yhje>0

		AND a.hous_id = #{house.id}


		AND a.goods_id = #{goods.id}


		AND a.store_id = #{store.id}


		AND a.create_date > #{startDate}


		AND a.create_date < #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT
	a.goods_id AS "goods.id",
	cg.name AS "goods.name",
	cg.sj AS "goods.sj",
	cg.big AS "goods.big.id",
	cuna.name AS "goods.big.name",
	cg.zong AS "goods.zong.id",
	cunb.name AS "goods.zong.name",
	cg.small AS "goods.small.id",
	cunc.name AS "goods.small.name",
	cg.specid AS "goods.spec.id",
	sp.name AS "goods.spec.name",
	SUM(a.nub) AS "nub",
	SUM(a.yhje) AS "yhje"
FROM c_ddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.rkckddinfoid = #{rkckddinfo.id}


		AND crkck.state = #{rkckddinfo.state}


		AND a.hous_id = #{house.id}


		AND a.goods_id = #{goods.id}


		AND a.supplier_id = #{supplier.id}


		AND a.store_id = #{store.id}


GROUP BY a.goods_id


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_ddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_ddinfo SET 
	cgzbinfo = #{cgzbinfo.id}
WHERE id = #{id}

UPDATE c_ddinfo SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
