mapper.select:
SELECT 

FROM c_goods a

WHERE a.id = #{id}

SELECT

FROM c_goods a

WHERE a.sort = #{sort}

SELECT
FROM c_goods a
a.del_flag = #{DEL_FLAG_NORMAL}
and a.id=#{id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.sort LIKE '%'||#{sort}||'%' '%'+#{sort}+'%' concat('%',#{sort},'%')
AND a.spbm LIKE '%'||#{spbm}||'%' '%'+#{spbm}+'%' concat('%',#{spbm},'%')
AND a.bandsid = #{bands.id}
AND ab.name LIKE '%'||#{bands.name}||'%' '%'+#{bands.name}+'%' concat('%',#{bands.name},'%')
AND a.tm = #{tm}
AND a.sccs = #{sccs}
AND a.supplierid = #{supplier.id}
AND cs.name LIKE '%'||#{supplier.name}||'%' '%'+#{supplier.name}+'%' concat('%',#{supplier.name},'%')
AND a.houid = #{houid}
AND cgc.name LIKE '%'||#{gclass.name}||'%' '%'+#{gclass.name}+'%' concat('%',#{gclass.name},'%')
AND (cgc.parent_ids LIKE concat('%',#{gclass.id},'%') OR a.gclassid=#{gclass.id})
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_goods SET
	cbj = #{cbj}
WHERE id = #{id}

UPDATE c_goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
