mapper.select:
SELECT 

FROM c_hgoods a

WHERE a.id = #{id}

SELECT 

FROM c_hgoods a


	a.del_flag = #{DEL_FLAG_NORMAL} AND cg.del_flag = #{DEL_FLAG_NORMAL}

		AND a.goodsid = #{goods.id}


		AND cg.name LIKE
		'%'||#{goods.name}||'%'
		'%'+#{goods.name}+'%'
		concat('%',#{goods.name},'%')


		AND a.housid = #{house.id}


		AND a.create_by >= #{updateDate} AND  a.create_by <= #{updateDate}


		AND ch.name LIKE
		'%'+#{house.name}+'%'
		'%'||#{house.name}||'%'
		concat('%',#{house.name},'%')




		ORDER BY ${page.orderBy}

SELECT

FROM c_hgoods a

	a.goodsid=#{goods.id}
	and a.housid=#{hous.id}

SELECT , SUM(a.nub) AS "unit" FROM c_hgoods a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.goodsid = #{goods.id}
AND cg.name LIKE '%'||#{goods.name}||'%' '%'+#{goods.name}+'%' concat('%',#{goods.name},'%')
AND a.housid = #{house.id}
AND ch.name LIKE '%'+#{house.name}+'%' '%'||#{house.name}||'%' concat('%',#{house.name},'%')
GROUP BY a.goodsid
ORDER BY ${page.orderBy}

SELECT cg.bandsid AS "goods.bands.id", cb.name AS "goods.bands.name", SUM(a.nub) AS "nub", SUM(a.nub*cg.sj)/SUM(a.nub) AS "goods.sj", SUM(a.nub*cg.cbj)/SUM(a.nub) AS "goods.cbj" FROM c_hgoods a LEFT JOIN c_goods cg ON cg.id=a.goodsid LEFT JOIN c_bands cb ON cb.id=cg.bandsid
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.goodsid = #{goods.id}
AND cg.name LIKE '%'||#{goods.name}||'%' '%'+#{goods.name}+'%' concat('%',#{goods.name},'%')
AND a.housid = #{house.id}
GROUP BY cg.bandsid
ORDER BY ${page.orderBy}

SELECT 

FROM c_hgoods a


	a.del_flag = #{DEL_FLAG_NORMAL} AND cg.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT

FROM c_hgoods a


	a.del_flag = #{DEL_FLAG_NORMAL} AND cg.del_flag = #{DEL_FLAG_NORMAL}
	AND a.housid = #{house.id} AND a.goodsid = #{goods.id}

mapper.update:
UPDATE c_hgoods SET
nub = #{nub}
WHERE id = #{id}

UPDATE c_hgoods a SET
 	nub=nub + #{nub}
WHERE a.housid=#{house.id} AND a.goodsid=#{goods.id}

UPDATE c_hgoods a SET
 	nub=nub - #{nub}
WHERE a.housid=#{house.id} AND a.goodsid=#{goods.id}

UPDATE c_hgoods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
