mapper.select:
SELECT 

FROM c_km a

WHERE a.id = #{id}

SELECT
FROM c_km a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.kmname LIKE '%'||#{kmname}||'%' '%'+#{kmname}+'%' concat('%',#{kmname},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_km a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_km SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
