mapper.select:
SELECT 

FROM c_rkckddinfo a

WHERE a.id = #{id}

SELECT o.* FROM( SELECT
FROM c_rkckddinfo a
a.create_by = #{createBy.id}
) o LIMIT ${fybs},10

SELECT 

FROM c_rkckddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.ddbh = #{ddbh}


		AND a.create_by = #{createBy.id}


		AND a.store_id = #{store.id}


		AND a.supplier_id = #{supplier.id}


		AND a.cHouse = #{cHouse.id}


		AND a.lx = #{lx}


		AND a.state = #{state}


		AND a.issp= #{issp}


		AND a.create_date > #{startDate}


		AND a.create_date < #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM c_rkckddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_by = #{createBy.id}


		AND a.ddbh = #{ddbh}


		AND a.supplier_id = #{supplier.id}


		AND a.lx = #{lx}


		AND a.id = #{id}


		AND a.state = #{state}


		AND a.issp= #{issp}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_rkckddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT COUNT(a.id) FROM c_rkckddinfo a
a.del_flag = '0' AND a.issp= '0'
AND a.state = #{_parameter}

