mapper.select:
SELECT 

FROM c_rkckddinfo a

WHERE a.id = #{id}

SELECT 

FROM c_rkckddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.lx = #{lx} AND a.state = #{state}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_rkckddinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_rkckddinfo SET 
	issp = #{issp},
	spr = #{spr},
	spsj = #{spsj}
WHERE id = #{id}

UPDATE c_rkckddinfo SET
	je = #{je}
WHERE id = #{id}

UPDATE c_rkckddinfo SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
