mapper.select:
SELECT 

FROM c_rkinfo a

WHERE a.id = #{id}

SELECT 

FROM c_rkinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.goodsid = #{goods.id}


		AND cg.name LIKE
			'%'||#{goods.name}||'%'
			'%'+#{goods.name}+'%'
			concat('%',#{goods.name},'%')


		AND a.houseid = #{house.id}


		AND ch.name LIKE
			'%'||#{house.name}||'%'
			'%'+#{house.name}+'%'
			concat('%',#{house.name},'%')


		AND a.create_date > #{startDate}


		AND a.create_date < #{endDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_rkinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT cgcl.name AS "goods.gclass.parent.name", cgc.name AS "goods.gclass.name", a.goodsid AS "goods.id", cg.big AS "goods.big.id", cuna.name AS "goods.big.name", cg.zong AS "goods.zong.id", cunb.name AS "goods.zong.name", cg.small AS "goods.small.id", cunc.name AS "goods.small.name", cg.specid AS "goods.spec.id", csp.name AS "goods.spec.name", cg.name AS "goods.name", cg.cbj AS "goods.cbj", csp.name AS "goods.spec.name", SUM(a.rknub*a.rkhcbj) AS "total", SUM(a.rknub*cg.sj) AS "goods.sj", a.rknub AS "rknub", a.rkhnub AS "rkhnub", a.rkqcbj AS "rkqcbj", a.rkhcbj AS "rkhcbj" FROM c_rkinfo a LEFT JOIN c_goods cg ON cg.id=a.goodsid LEFT JOIN c_gclass cgc ON cgc.id=cg.gclassid LEFT JOIN c_gclass cgcl ON cgcl.id=cgc.parent_id LEFT JOIN c_unit cuna ON cg.`big`=cuna.`id` LEFT JOIN c_unit cunb ON cg.`zong`=cunb.`id` LEFT JOIN c_unit cunc ON cg.`small`=cunc.`id` LEFT JOIN c_spec csp ON csp.id=cg.specid
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.goodsid = #{goods.id}
AND cg.name LIKE '%'||#{goods.name}||'%' '%'+#{goods.name}+'%' concat('%',#{goods.name},'%')
AND a.houseid = #{house.id}
AND ch.name LIKE '%'||#{house.name}||'%' '%'+#{house.name}+'%' concat('%',#{house.name},'%')
AND a.create_date > #{startDate}
AND a.create_date < #{endDate}
GROUP BY a.goodsid
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE c_rkinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
