mapper.select:
SELECT 

FROM c_sclass a

WHERE a.id = #{id}

SELECT 

FROM c_sclass a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM c_sclass a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_sclass SET 	
	name = #{name},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE c_sclass SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
