mapper.select:
SELECT 

FROM c_store a

WHERE a.id = #{id}

SELECT
FROM c_store a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.dpmc LIKE '%'||#{dpmc}||'%' '%'+#{dpmc}+'%' concat('%',#{dpmc},'%')
AND a.khjc LIKE '%'||#{khjc}||'%' '%'+#{khjc}+'%' concat('%',#{khjc},'%')
AND a.yyzz = #{yyzz}
AND a.sclassid = #{sclass.id}
AND (area.parent_ids LIKE concat('%',#{area.id},'%')OR a.areaid = #{area.id})
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_store a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_store SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
