mapper.select:
SELECT 

FROM c_supplier a

WHERE a.id = #{id}

SELECT

FROM c_supplier a

WHERE a.name = #{name}

SELECT

FROM c_supplier a

WHERE a.code = #{code}

SELECT o.* FROM( SELECT
FROM c_supplier a
) o LIMIT ${fybs},10

SELECT
FROM c_supplier a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' concat('%',#{code},'%')
AND a.sfzz = #{sfzz}
AND a.yyzz LIKE '%'||#{yyzz}||'%' '%'+#{yyzz}+'%' concat('%',#{yyzz},'%')
AND a.gysjc LIKE '%'||#{gysjc}||'%' '%'+#{gysjc}+'%' concat('%',#{gysjc},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_supplier a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_supplier SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
