mapper.select:
SELECT 

FROM c_ykinfo a

WHERE a.id = #{id}

SELECT o.* FROM( SELECT
FROM c_ykinfo a
) o LIMIT ${fybs},10

SELECT
FROM c_ykinfo a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.starthouid = #{startHouse.id}
AND a.order_code = #{orderCode}
AND ch.name LIKE '%'||#{startHouse.name}||'%' '%'+#{startHouse.name}+'%' concat('%',#{startHouse.name},'%')
AND a.endhouid = #{endHouse.id}
AND cho.name LIKE '%'||#{endHouse.name}||'%' '%'+#{endHouse.name}+'%' concat('%',#{endHouse.name},'%')
AND a.storeid = #{goods.id}
AND cg.name LIKE '%'||#{goods.name}||'%' '%'+#{goods.name}+'%' concat('%',#{goods.name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT
FROM c_ykinfo a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.starthouid = #{startHouse.id}
AND ch.name LIKE '%'||#{startHouse.name}||'%' '%'+#{startHouse.name}+'%' concat('%',#{startHouse.name},'%')
AND a.endhouid = #{endHouse.id}
AND cho.name LIKE '%'||#{endHouse.name}||'%' '%'+#{endHouse.name}+'%' concat('%',#{endHouse.name},'%')
AND a.storeid = #{goods.id}
AND cg.name LIKE '%'||#{goods.name}||'%' '%'+#{goods.name}+'%' concat('%',#{goods.name},'%')
GROUP BY a.starthouid,a.storeid
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM c_ykinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE c_ykinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
